import React, { useState } from "react";
import classes from "./Expense.module.css";

const Expense = (props) => {
  const [isEditing, setIsEditing] = useState(false);

  return (
    <div className={classes["budget-list"]}>
      <p>{props.purchaseDate}</p>
      <p>{props.category}</p>
      <p>{`${String.fromCharCode(163)}${props.amount.toFixed(2)}`}</p>
      <p>{props.description}</p>
      <a className={classes["edit-expense-link"]}>
        <button className={classes["edit-expense-btn"]}>Edit</button>
      </a>
      <form
        class="budget-list-edit-form"
        action="/editexpense"
        id="expense"
        method="post"
      >
        <input
          className={classes["edit-expense-input"]}
          type="hidden"
          name="id"
          value={props.id}
        />
        <input
          className={classes["edit-expense-input"]}
          type="date"
          name="purchaseDate"
          value={props.purchaseDate}
          placeholder={props.purchaseDate}
        />
        <select
          className={classes["edit-expense-input"]}
          name="category"
          value={props.category}
          placeholder={props.category}
        >
          <option value="" disabled selected>
            Select something...
          </option>
          <option value="DATES">Dates</option>
          <option value="MISC">Misc</option>
          <option value="FUEL">Fuel</option>
        </select>
        <input
          className={classes["edit-expense-input"]}
          type="text"
          name="amount"
          value={props.amount}
          placeholder={props.amount}
        />
        <input
          className={classes["edit-expense-input"]}
          type="text"
          name="description"
          value={props.description}
          placeholder={props.description}
        />
        <input
          className={classes["edit-expense-input"]}
          name="submit-login"
          type="submit"
          value="submit"
        />
      </form>
      <a className={classes["delete-expense-link"]} href="/delete/expense.id">
        <button className={classes["delete-expense-btn"]}>Delete</button>
      </a>
      <div>
        <input type="checkbox" id="ignore-expense-checkbox" />
      </div>
    </div>
  );
};

export default Expense;
